import { Button } from "@material-ui/core";
import moment from "moment";
import React, { useEffect, useState } from "react";
import {
  buttonLoader,
  showErrorToast,
  showSuccessToast,
} from "../../../components/common";
import { TablePicker } from "../../../components/table-picker";
import { CommonController } from "../../../_redux/controller/common.controller";
const AddTicketCommercial = ({ changeTab, previewId, ...props }) => {
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [loading, setLoading] = useState(false);
  const [showPicker, setShowPicker] = useState(false);
  const togglePicker = () => setShowPicker(!showPicker);
  const [errors, setErrors] = useState({});
  const [formData, setFormData] = useState({
    tran_id: previewId ? previewId : "0",
    ticket_id: "",
    date: moment().format("DD-MM-yyyy"),
    charges: "",
    remarks: "",
    user_id: localStorage.getItem("userId"),
  });
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };
  const validateForm = () => {
    let errors = {};
    if (formData.charges === "") {
      errors.charges = "Charges required";
    } else {
      delete errors.charges;
    }
    if (formData.remarks ==="") {
      errors.remarks = "Remarks required";
    } else {
      delete errors.remarks;
    }

    const valid = Object.keys(errors).length === 0 ? true : false;
    setErrors(errors);
    return valid;
  };

  const onSubmit = async () => {
    try {
      setLoading(true);
      if (validateForm()) {
        await CommonController.commonApiCallFilter(
          "TicketCommercial/TicketCommericialInsert",
          formData,
          "post"
        ).then((data) => {
          if (data.valid) {
            showSuccessToast("Ticket Commercial Successfully");
            changeTab(0);
          } else {
            showErrorToast(data.msg);
          }
        });
      }
      setLoading(false);
    } catch (err) {
      showErrorToast(err);
    }
  };

  const getTicketById = async () => {
    try {
      await CommonController.commonApiCallFilter(
        "TicketCommercial/TicketCommericialPreview",
        { tran_id: previewId },
        "post"
      ).then((data) => {
        setSelectedTicket(data);
        setFormData({
          ...formData,
          charges: data.charges,
          remarks: data.remarks,
          ticket_id: data.ticket_id,
        });
      });
    } catch (err) {
      showErrorToast(err);
    }
  };

  useEffect(() => {
    if (previewId) {
      getTicketById();
    }
  }, [previewId]);

  const newColumns = [
    {
      id: "id",
      label: "Sr. no",
      numeric: false,
      disablePadding: true,
    },
    {
      id: "date",
      label: "Date",
      numeric: false,
      disablePadding: true,
    },
    {
      id: "ticket_no",
      label: "Ticket No",
      numeric: false,
      disablePadding: true,
    },
    {
      id: "menu",
      label: "Menu",
      numeric: false,
      disablePadding: true,
    },
    {
      id: "transaction",
      label: "Transaction",
      numeric: false,
      disablePadding: true,
    },
    {
      id: "type",
      label: "Type",
      numeric: false,
      disablePadding: true,
    },
    {
      id: "priority",
      label: "Priority",
      numeric: false,
      disablePadding: true,
    },
    {
      id: "details",
      label: "Details",
      numeric: false,
      disablePadding: true,
    },
    {
      id: "generated_by",
      label: "Generated By",
      numeric: false,
      disablePadding: true,
    },
    {
      id: "status",
      label: "Status",
      numeric: false,
      disablePadding: true,
    },
  ];

  const handlePickTicket = (ticket) => {
    setFormData({ ...formData, ticket_id: String(ticket.id) });
    setSelectedTicket(ticket);
    setShowPicker(false);
  };

  return (
    <div className="container-fluid mt-5">
      <div className="row">
        <div className="col-md-12 text-right">
          {!showPicker && (
            <Button
              variant="contained"
              onClick={togglePicker}
              disableElevation
              color="primary"
            >
              Pick Ticket
            </Button>
          )}
