import React, { useEffect, useState } from 'react';
import { TextField, Button } from '@material-ui/core';
import { Alert } from '@material-ui/lab';
import { CommonController } from '../../../../_redux/controller/common.controller';
import { showErrorToast, showSuccessToast } from '../../../../components/common';
const AddOrEditGroup = (props) => {
    console.log(props.editData)
    const [groupValues, setGroupValues] = useState({
        transaction_id:0,
        display_name: "",
        sequence:null,
        parent_id:null
    });
    const [showMessage, setMessage] = useState({
        type: "",
        msg: ""
    })
    const insertForm = async () => {
        try {
            let body = {
                user_name: localStorage.getItem("userName"),
                user_id: localStorage.getItem("userId"),
                sequence: groupValues.sequence,
                display_name: groupValues.display_name,
                transaction_id: groupValues.transaction_id
            }
            await CommonController.commonApiCallFilter(
                "master/insert_category",
                body,
                "post",
                "node"
            ).then(result => {
                if (result.status == 200) {
                    showSuccessToast(result.message)
                    props.onClose(0);
                }
            })
        } catch (err) {
            showErrorToast(err)
        }
    }
    useEffect(() => {
        if(props.editData){
        setGroupValues(props.editData)
        }
    }, [props.editData]);
    const onSave = () => {
        insertForm()
    }
    const handleOnChange = (event) => {
        setGroupValues({
            ...groupValues,
            [event.target.name]: event.target.value
        });
    }
    const onCancelClick = () => {
        props.onClose(0);
    }
    return <React.Fragment>
        <div className="container-fluid">
            <div className="row">
                <div className="col-md-4">
                    <TextField label="Transaction ID" name="transaction_id"disabled value={groupValues.transaction_id} onChange={handleOnChange} fullWidth variant="outlined" size="small" />
                </div>
                <div className="col-md-4">
                    <TextField label="Display Name" name="display_name" value={groupValues.display_name} onChange={handleOnChange} fullWidth variant="outlined" size="small" />
                </div>
                <div className="col-md-4">
                    <TextField multiline label="sequence" value={groupValues.sequence} onChange={handleOnChange} name="sequence" fullWidth variant="outlined" size="small" type='number' />
                </div>
                <div className="col-md-4">
                    <TextField multiline label="Parent Id" value={groupValues.parent_id} onChange={handleOnChange} name="parent_id" fullWidth variant="outlined" size="small" type='number' />
                </div>
                <div className="col-md-12 mt-3 text-right">
                    <Button variant="contained" className="mr-2" onClick={onCancelClick} disableElevation>Cancel</Button>
                    {groupValues.type == "preview" ? "" : <Button variant="contained" onClick={onSave} color="primary" disableElevation>Save</Button>}
                </div>
                {showMessage.type != "" ? <Alert severity={showMessage.type}>{showMessage.msg}</Alert> : null}
            </div>
        </div>
    </React.Fragment>
}
export default AddOrEditGroup;
