import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { CommonController } from "../../../_redux/controller/common.controller";
import ContactPerson from "./contact_person";
import GeneralInfo from "./generalInfo";
import AccountTNC from "./tnc";
import { Button } from "@material-ui/core";
import { showErrorToast, showSuccessToast } from "../../../components/common";
const AddAccountMaster = () => {
  const selectedIdResponse = useSelector(
    (state) => state.AllReducersMaster.accountId
  );
  console.log(selectedIdResponse)
  const [selectedIndex, setSeletedIndex] = useState(0);
  const [formData, setFormData] = useState({

    company_id:0,
    company_name:"",  
    short_name:"",
    group_id :"", 
    group_name:"",
    address1 :"",  
    address2 :"",  
    pin_id :"",  
    pin_code:"",  
    region_id :"",  
    region_name:"",  
    phone1:"",  
    phone2:"",  
    mobile:"",  
    fax:"",  
    email :"",  
    website :"",  
    rating_id:"",  
    rating_name :"",  
    // mark_engg :"",  
    name:"",
    se_id:"",  
    Engg_name :"",  
    // sim_enng
    remarks:"" ,  
    distance:"",  
    credit_limit :"",  
    credit_period:"",  
    range:"",  
    division:"",  
    comm:"",  
    ecc_no:"",  
    ser_tax_no:"",  
    pan_no:"",  
    tin_no:"",  
    cst_no:"",  
    lst_no:"",  
    pla_no:"",  
    edit:"",  
    hide:"",  
    boarding_id:"",  
    delivery_id:"",  
    exciseduty_id:"",  
    finance_id:"",  
    freight_id:"",  
    insurance_id :"",  
    inspection_id :"",  
    ld_id:"",  
    loading_id:"",  
    mode_of_dispatch_id:"",  
    octroi_id:"",  
    payment_id:"",  
    pf_id:"",  
    salestax_id:"",  
    servicetax_id:"",  
    validity_id:"",  
    conveyance_id:"",  
    travel_id:"",  
    user_name: localStorage.getItem("userName"),
user_id: localStorage.getItem("userId"),
    new_identity:"",
    account_type:"",
    

    ///
    // company_id: "",
    // company_name: "",
    // datetime: "",
    // short_name: "",
    // mobile: "",
    // // mark_engg: "",
    // name:"",
    // edit: "",
    // hide: "",
    // district: "",
    // state: "",
    // city: "",
    // region_name: "",
    // group_name: "",
    // verified: null,
    // group_id: "",
    // address1: "",
    // address2: "",
    // pin_code_id: "",
    // pin_code_no: "",
    // region_id: "",
    // phone1: "",
    // phone2: "",
    // fax: "",
    // email: "",
    // website: "",
    // rating_id: "",
    // rating_name: "",
    // se_id: "",
    // Engg_name: "",
    // // siem_engg: "",
    // remarks: "",
    // distance: "",
    // credit_limit: "",
    // credit_period: "",
    // range: "",
    // division: "",
    // comm: "",
    // ecc_no: "",
    // ser_tax_no: "",
    // pan_no: "",
    // tin_no: "",
    // cst_no: "",
    // lst_no: "",
    // pla_no: "",
    // user_id: localStorage.getItem("userId"),
    // user_name: localStorage.getItem("userName"),
    // new_identity: null,
    // pf_id: "",
    // exciseduty_id: "",
    // salestax_id: "",
    // freight_id: "",
    // insurance_id: "",
    // inspection_id: "",
    // mode_of_dispatch_id: "",
    // delivery_id: "",
    // octroi_id: "",
    // servicetax_id: "",
    // travel_id: "",
    // conveyance_id: "",
    // loading_id: "",
    // boarding_id: "",
    // ld_id: "",
    // validity_id: "",
    // payment_id: "",
    // finance_id: "",
    // tcs_per: "",
    // add_user_id: "",
    // edit_user_id: "",
    // edit_user_name: "",
    // add_user_name: "",
    // edatetime: "",
    // cpersonList: [],
    // partyList: [],
    // account_type:"",
  });

  useEffect(() => {
    if (selectedIdResponse) {
      CommonController.commonApiCallFilter("Account/AccountMasterPreview", {
        company_id: selectedIdResponse,
      }).then((data) => {
        setFormData(data);
      });
    }
  }, [selectedIdResponse]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };
  const handleAutoChange = (key1, key2, value) => {
    console.log(value[key1])
    console.log(value)
    console.log(key1)
    console.log(key2)
    let tempFormData = { ...formData };

    if (key1.trim() != "") {
      tempFormData[key1] = value[key1];
    }

    if (key2.trim() != "") {
      tempFormData[key2] = value[key2]
    }
    setFormData(tempFormData);
  };

  console.log(formData);

  const saveForm = () => {
    let _formData = { ...formData };

    if (selectedIdResponse) {
      _formData.user_id = localStorage.getItem("userId");
      _formData.user_name = localStorage.getItem("userName");
    }

    CommonController.commonApiCallFilter(
      "master/insert_account_master",
      _formData,"post","node"
    ).then((data) => {
      if (data.status===200) {
        showSuccessToast(
          `Account Details ${
            selectedIdResponse ? "updated" : "saved"
          } successfully`
        );
      }
    });
  };

  return (
    <React.Fragment>
      <ul className="nav nav-tabs nav-tabs-line">
        <li className="nav-item">
          <a
            className={`nav-link ` + (selectedIndex === 0 ? "active" : "")}
            onClick={() => setSeletedIndex(0)}
          >
            General Infomation
          </a>
        </li>
        <li className="nav-item">
          <a
            className={`nav-link ` + (selectedIndex === 1 ? "active" : "")}
            onClick={() => setSeletedIndex(1)}
          >
            Contact Person
          </a>
        </li>
        <li className="nav-item">
          <a
            className={`nav-link ` + (selectedIndex === 2 ? "active" : "")}
            onClick={() => setSeletedIndex(2)}
          >
            Terms & Conditions
          </a>
        </li>
      </ul>
      <div className="tab-content pt-7">
        {selectedIndex === 0 && (
          <GeneralInfo
            formData={formData}
            handleChange={handleChange}
            handleAutoChange={handleAutoChange}
          />
        )}
        {selectedIndex === 1 && (
          <ContactPerson
            formData={formData}
            handleChange={handleChange}
            handleAutoChange={handleAutoChange}
            removeIndex={(data)=>setFormData({...formData,cpersonList:data})}
            handleCPersonList={(arr) =>{
              let cperson={...formData};
              cperson.cpersonList.push(arr);
              console.log(cperson);
              setFormData(cperson);
            }
            }
          />
        )}

        {selectedIndex === 2 && (
          <AccountTNC formData={formData} handleChange={handleChange} />
        )}
      </div>
      <div className="col-md-12 text-right">
        {selectedIndex !== 0 && (
          <Button
            variant="contained"
            className="mr-2"
            onClick={() => setSeletedIndex(selectedIndex - 1)}
            disableElevation
          >
            Back
          </Button>
        )}
        {selectedIndex !== 2 && (
          <Button
            variant="contained"
            onClick={() => setSeletedIndex(selectedIndex + 1)}
            color="primary"
            className="mr-2"
            disableElevation
          >
            Next
          </Button>
        )}

     {selectedIndex==2&&<Button
          variant="contained"
          onClick={saveForm}
          color="primary"
          disableElevation
        >
          Submit
        </Button>
        }   
      </div>
    </React.Fragment>
  );
};

export default AddAccountMaster;
