  }
},[accountMasterFilter,params])
const handleUpdateFilterData=()=>{
  let body={filterpage:{...params},filterData:{...accountMasterFilter}}
  body.user_id=localStorage.getItem("userId")
  body.browse_id=1
  dispatch(updateFilterData(body))
  if(updatefilterjsonData.status==200){
    dispatch(getFilterData(1))
  }
  
  console.log("cfbhjn")
}
useEffect(()=>{
  setLoading(false)
  if(browsedata?.data?.length>0){
    setBrowseListData(browsedata.data)
    setTotalRecords(browsedata.totalRecords)
  }
},[browsedata])
  let columns = [
    {
      field: "ID",
      headerName: "ID",
      width: 70,
      hide: false,
    },
    {
      field: "Region",
      headerName: "Region",
      width: 180,
      hide: false,
    },
    {
      field: "short_name",
      headerName: "Short Name",
      renderCell: (params) => (
        <LightTooltip title={params.row.short_name}>
          <span>{params.row.short_name}</span>
        </LightTooltip>
      ),
      width: 200,
      hide: false,
    },
    {
      field: "Company",
      headerName: "Company",
      renderCell: (params) => (
        <LightTooltip title={params.row.Company}>
          <span>{params.row.Company}</span>
        </LightTooltip>
      ),
      width: 180,
      hide: false,
    },
    {
      field: "Address",
      headerName: "Address",
      renderCell: (params) => (
        <LightTooltip title={params.row.Address}>
          <span>{params.row.Address}</span>
        </LightTooltip>
      ),
      width: 300,
      hide: false,
    },
    {
      field: "Pin",
      headerName: "Pin",
      width: 80,
      hide: false,
    },
    {
      field: "Mobile",
      headerName: "Mobile",
      renderCell: (params) => (
        <LightTooltip title={params.row.Mobile}>
          <span>{params.row.Mobile}</span>
        </LightTooltip>
      ),
      width: 150,
      hide: false,
    },
    {
      field: "Email",
      headerName: "Email",
      renderCell: (params) => (
        <LightTooltip title={params.row.Email}>
          <span>{params.row.Email}</span>
        </LightTooltip>
      ),
      width: 200,
      hide: false,
    },
    {
      field: "verified",
      headerName: "Verified",
      renderCell: (params) => (
        <FormControlLabel
          className={"formControlLabel"}
          control={
            <Checkbox
              defaultChecked={params.row.edit === "True"}
              size="small"
              color="primary"
              onChange={(event) =>
                updateVerifiedStatus(event.target.checked, params.id)
              }
              inputProps={{ "aria-label": "checkbox with small size" }}
            />
          }
          label={
            <span
              className={
                "font_13 " +
                (params.row.edit === "True" ||
                tempVerifed.indexOf(params.row.id) > -1
                  ? "text-success"
                  : "text-danger")
              }
            >
              {params.row.edit === "True" ||
              tempVerifed.indexOf(params.row.id) > -1
                ? "Verified"
                : "Not Verified"}
            </span>
          }
        />
      ),
      width: 120,
      hide: false,
    },
    {
      field: "hide",
      headerName: "Hide",
      renderCell: (params) => (
        <Checkbox
          size="small"
          color="primary"
          defaultChecked={params.row.hide === "True"}
          onChange={(event) =>
            updateHideStatus(event.target.checked, params.id)
          }
          // onChange={() => console.log(params.id)}
          inputProps={{ "aria-label": "checkbox with small size" }}
        />
      ),
      width: 80,
      hide: false,
    },
    {
      field: "mark_engg",
      headerName: "Markt. Engg",
      renderCell: (params) => (
        <LightTooltip title={params.row.mark_engg}>
          <span>{params.row.mark_engg}</span>
        </LightTooltip>
      ),
      width: 150,
      hide: false,
    },

    {
      field: "datetime",
      headerName: "Date Time",
      width: 120,
      hide: false,
    },
    // {
    //   field: "",
    //   headerName: "Actions",
    //   renderCell: (params) => (
    //     <ActionButtons
    //       onPreview={
    //         getBrowseUserRight(getuserRightListResponse)?.level == 1
    //           ? () => handleEdit(params.row.id)
    //           : null
    //       }
    //       onEdit={
    //         getBrowseUserRight(getuserRightListResponse)?.level == 1
    //           ? () => handleEdit(params.row.id)
    //           : null
    //       }
    //       // onDelete={
