import React, { useEffect, useState } from 'react';
import { TextField, Button } from '@material-ui/core';
import { Alert } from '@material-ui/lab';
import { CommonController } from '../../../../../_redux/controller/common.controller';
import { showErrorToast, showSuccessToast } from '../../../../../components/common';
import { SimpleTable } from '../../../../../components/basic-table';
const AddOrEditGroup = (props) => {
    const [groupValues, setGroupValues] = useState({
        tab_id: "",
        group_name: "",
        description: "",
        tabArray:[]
    });
    const [showMessage, setMessage] = useState({
        type: "",
        msg: ""
    })
    const tabArrayDelet=(e)=>{
        console.log("e")
    }
    const listColumn = [
        {
          id: "box_no",
          numeric: false,
          disablePadding: false,
          label: "Box No",
        },
        {
          id: "description",
          numeric: false,
          disablePadding: false,
          label: "Description",
        },
        {
          id: "qty",
          numeric: false,
          disablePadding: false,
          label: "Qty",
        },
      ];
    const insertForm = async () => {
        try {
            let body = {
                user_name: localStorage.getItem("userName"),
                user_id: localStorage.getItem("userId"),
                description: groupValues.description,
                group_name: groupValues.group_name,
                tab_id: groupValues.tab_id
            }
            await CommonController.commonApiCallFilter(
                "master/insert_group",
                body,
                "post",
                "node"
            ).then(result => {
                if (result.status == 200) {
                    showSuccessToast(result.message)
                    setGroupValues({
                        tab_id: "",
                        group_name: "",
                        description: ""
                    })
                }
            })
        } catch (err) {
            showErrorToast(err)
        }
    }
    useEffect(() => {
        setGroupValues(props.editData)
    }, [props.editData]);
    const onSave = () => {
        insertForm()
    }
    const handleOnChange = (event) => {
        setGroupValues({
            ...groupValues,
            [event.target.name]: event.target.value
        });
    }
    const onCancelClick = () => {
        props.onClose(0);
    }
    return <React.Fragment>
        <div className="container-fluid">
            <div className="row">
                <div className="col-md-4">
                    <TextField label="Tab ID" name="tab_id" value={groupValues.tab_id} onChange={handleOnChange} fullWidth variant="outlined" size="small" />
                </div>
                <div className="col-md-4">
                    <TextField label="Tab Name" name="group_name" value={groupValues.group_name} onChange={handleOnChange} fullWidth variant="outlined" size="small" />
                </div>

             {groupValues.tabArray.length>0&&<SimpleTable columns={listColumn} rows={groupValues.tabArray} onDelete={tabArrayDelet}  />}

             <hr />
      <div className="container-fluid">
        {/* <h1>Add Box:</h1> */}
        <div className="row mt-5">
          <div className="col-md-3 mb-3">
            <TextField
              label="Box No"
              fullWidth
              onChange={handleChange}
              value={box_no}
              variant="outlined"
              name="box_no"
              size="small"
            />
          </div>
          <div className="col-md-4 mb-3">
            <TextField
              label="Description"
              fullWidth
              onChange={handleChange}
              value={description}
              variant="outlined"
              name="description"
              size="small"
            />
          </div>
          <div className="col-md-3 mb-3">
            <TextField
              label="Qty"
              fullWidth
              onChange={handleChange}
              value={qty}
              variant="outlined"
              name="qty"
              type="number"
              size="small"
            />
          </div>
          <div className="col-md-2 mb-3 pl-5">
            <Button
              color="primary"
              // className="mr-2"
              // disableElevation
              variant="contained"
              onClick={submitAddBox}
            >
              Add New
            </Button>
          </div>
        </div>
      </div>
                <div className="col-md-12 mt-3 text-right">
                    <Button variant="contained" className="mr-2" onClick={onCancelClick} disableElevation>Cancel</Button>
                    {groupValues.type == "preview" ? "" : <Button variant="contained" onClick={onSave} color="primary" disableElevation>Save</Button>}
                </div>
                {showMessage.type != "" ? <Alert severity={showMessage.type}>{showMessage.msg}</Alert> : null}
            </div>
        </div>
    </React.Fragment>
}
export default AddOrEditGroup;
